import styled from 'styled-components'
import img from './city.png'


const Container = styled.div`
  margin-left: auto;
  margin-right: auto;
  max-width: 100%;
  height: 100vh;
  background-repeat: no-repeat;
  background-position: cover;

  padding-left: 16px;
  padding-right: 16px;
  h1 { 
    color: white;


  }

  ${({ theme }) => theme.mediaQueries.sm} {
    padding-left: 24px;
    padding-right: 24px;
  }
`

export default Container
